import React from "react";
// import './App.css';
import {useState, useEffect} from "react";
import {BrowserRouter as Router, Outlet, Route, Routes, link, Link} from "react-router-dom";
import {MonsterList,MonsterPage} from "./Components";
import monster from "./img/monster.png";
import welcoming from "./img/wellcoming.png";
function App() {

    return (
   <div>
       <Router>
           <Routes>
               <Route path='/' element={<Header />}/>
               <Route path='/monsters' element={<MonsterList />}/>
               <Route path=':monsterID' element={<MonsterPage />}/>
           </Routes>

       </Router>

       {/*<MonsterList />*/}
   </div>
    );
}
const Header  = () => {
    return(
        <div>
            <header className="header">
                <ul className="nav-bar">
                    <img src={monster} alt="monster" className="logo"/>
                    <li className="nav-bar__item">
                        <Link to="/" className="nav-bar__link">صفحه اصلی</Link>
                    </li>
                    <li className="nav-bar__item">
                        <Link to="/monsters" className="nav-bar__link">هیولاها</Link>
                    </li>

                    <li className="nav-bar__item">
                        <Link to="/monsters" className="nav-bar__link">هیولاهای محبوب من</Link>
                    </li>

                    <li className="nav-bar__item">
                        <Link to="/" className="nav-bar__link">بلاگ</Link>
                    </li>

                </ul>
<Outlet/>
            </header>
            <div>
                <img className='img-welcome' src={welcoming}/>
            </div>
        </div>

    )
};

  export default App;
