import React from "react";
import {useEffect,useState} from "react";
import {MonsterCard} from "..";
import {Container,Row,Col,FormControl} from "react-bootstrap";
const MonsterList = () => {
    const [monsterList , setMonsterList]= useState([{
        id: 1,
        name: "هیولای آنتن دار",
        username:"zahraaa",
        website: "قشنگترین هیولای آنتنی جهانم"
    },
        {
            id: 1,
            name: "هیولای استقلالی",
            username:"Reza",
            website: "هیولای استقلالی هستم"
        },
        {
            id: 1,
            name: "هیولای مو قشنگ",
            username:"masi",
            website: "هیولاییم کهدنیای صورتی خودمو دارم"
        },


    ])
    return(
       <Container>
           <Row>
               <Col>
               <FormControl className="m-3 p-2" type="text" placeholder="میتونی در من جستوجو کنی... :)" readOnly />
               </Col>
           </Row>
           <Row>
               {
                  monsterList.map((monster) =>(
                      <Col key={monster.id} xs={12} sm={6} md={4} lg={3}>
                          <MonsterCard name={monster.name} description={monster.website} img={"https://robohash.org/"+monster.username}/>
                      </Col>
                  ))
               }
               {/*<Col xs={12} sm={6} md={4} lg={3}>*/}
               {/*    <MonsterCard name='هیولای خشمگین' description="من یه هیولا عصبانی هستم >_< " img="https://robohash.org/119"/>*/}
               {/*</Col>*/}
               {/*<Col xs={12} sm={6} md={4} lg={3}>*/}
               {/*    <MonsterCard/>*/}
               {/*</Col>*/}
               {/*<Col xs={12} sm={6} md={4} lg={3}>*/}
               {/*    <MonsterCard/>*/}
               {/*</Col>*/}
               {/*<Col xs={12} sm={6} md={4} lg={3}>*/}
               {/*    <MonsterCard/>*/}
               {/*</Col>*/}

           </Row>
       </Container>
    )
}

export default MonsterList;
