import React, {useEffect} from "react";
import {MonsterCard} from "..";
import notFonud from "../../img/not-found.jpg";
import {Col, Container, FormControl, InputGroup, Row} from "react-bootstrap";
import {Link} from "react-router-dom";
const MonsterListPouplar =() => {
    const [monsterListPopular, setMonsterListPouplar] = useState(
        [{
            id: 1,
            name: "هیولای آنتن دار",
            username: "zahraaa",
            website: "قشنگترین هیولای آنتنی جهانم"
        },
            {
                id: 1,
                name: "هیولای استقلالی",
                username: "Reza",
                website: "هیولای استقلالی هستم"
            },
            {
                id: 1,
                name: "هیولای مو قشنگ",
                username: "masi",
                website: "هیولایی با دنیای صورتی "
            },
        ])
    const getMonster = () => {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(data => setMonsterListPouplar(data));

    }
    useEffect(() => {
        getMonster();
    })

    function MonsterPopular() {
        return (
            <Container>
                <Row>
                    <Col>
                        <InputGroup className="m-3 p-2">
                            <FormControl
                                placeholder="میتونی در من جستوجو کنی... :)"
                                onChange={e => setFilter(e.target.value)}
                                aria-label="Recipient's username"
                                aria-describedby="basic-addon2"
                            />
                            <InputGroup.Text id="basic-addon2">جستوجو</InputGroup.Text>
                        </InputGroup>
                        {/*<FormControl className="m-3 p-3" type="text" placeholder="میتونی در من جستوجو کنی... :)" />*/}
                    </Col>
                </Row>
                <Row>
                    {/*{monsterList.map((monster) =>*/}
                    {monsterListPopular.filter(monster => monster.name.toLowerCase().includes(filter.toLowerCase())).length === 0 ?
                        <div className="not-found-monster"><h2>هیچ هیولایی نیست</h2><img src={notFonud}/></div> :
                        monsterListPopular.filter(monster => monster.name.toLowerCase().includes(filter.toLowerCase())).map((monster) => (
                            <Col className="mb-3 " key={monster.id} xs={12} sm={6} md={4} lg={3}>
                                <Link className='text-decoration-none text-center' to='/card'>
                                    <MonsterCard name={monster.name} description={monster.website}
                                                 img={"https://robohash.org/" + monster.username}/>
                                </Link>
                            </Col>
                        ))
                    }
                    <Col xs={12} sm={6} md={4} lg={3}>
                        <MonsterCard name='هیولای خشمگین' description="من یه هیولا عصبانی هستم >_< "
                                     img="https://robohash.org/119"/>
                    </Col>
                    <Col xs={12} sm={6} md={4} lg={3}>
                        <MonsterCard/>
                    </Col>
                    <Col xs={12} sm={6} md={4} lg={3}>
                        <MonsterCard/>
                    </Col>
                    <Col xs={12} sm={6} md={4} lg={3}>
                        <MonsterCard/>
                    </Col>

                </Row>
            </Container>
        )
    }
}
export default MonsterListPouplar;
